(1, 'feed_html', '<h1>[*feed_title*]</h1>\n<p>[<a href=\"[*feed_html*]\">HTML</a>]\n[<a href=\"[*feed_link*]\">XML</a>]  <admin feed,[*feed_id*]></p>\n<p>Author: <span class=\"feed_author\"><keylist db=feed;id=[*feed_id*];keytable=author></span><br />\n [*feed_description*]<br>\n<table cellmargin=0 cellpadding=3 border=1>\n<tr><td>Language</td><td>[*feed_language*]</td></tr>\n<tr><td>Copyright</td><td>[*feed_rights*]</td></tr>\n<tr><td>Last Updated</td><td> [*feed_lastBuildDate*] </td></tr>\n<tr><td>Updates</td><td> [*feed_updatePeriod*] </td></tr>\n<tr><td>Generator</td><td> [*feed_generator*] </td></tr>\n</table>\n</p>\n\n<p><keylist db=feed;id=[*feed_id*];keytable=post;separator=BR;heading=Recent Posts></p>\n\n<p><keylist db=feed;id=[*feed_id*];keytable=link;separator=BR;heading=Recent Links></p>\n\n<p><keyword db=media;feedid=[*feed_id*];heading=Media></p>\n\n<p>Information about this feed is obtained from the <a href=\"[*feed_link*]\">feed [*feed_type*] source</a>. Feed owners may supply additional information by updating their file or by sending email to <a href=\"mailto:stephen@downes.ca\">stephen@downes.ca</a>\n <admin feed,[*feed_id*]></p>', NULL, NULL),
(146, 'post_link_html', '<!-- Begin Post -->\r\n\r\n<div class=\"post_header\" style=\"padding-bottom:10px;border-bottom: 1px solid black;\">\r\n<h2><a href=\"<st_url>post/[*post_id*]/rd\">[*post_title*]</a></h2>\r\n<keylist db=post;id=[*post_id*];keytable=author;postfix=, >\r\n<keylist db=post;id=[*post_id*];keytable=feed;postfix=, >\r\n<date format=nice;time=[*post_crdate*]><br><i>Commentary by Stephen Downes</i></div>\r\n\r\n<image display><div class=\"post_body\"> [*post_description*] <enclosures format=html></div><div class=\"post_services\">Views: [*post_hits*] today, [*post_total*] total (since January 1, 2017).<!box vote><!box hits><!box addthis><box topics></div>   <!--- Ends Services -->   \r\n<div id=\"disqus_thread\"></div>    <script type=\"text/javascript\">        /* * * CONFIGURATION VARIABLES: EDIT BEFORE PASTING INTO YOUR WEBPAGE * * */        var disqus_shortname = &#39;downes-ca&#39;; // required: replace example with your forum shortname        /* * * DON&#39;T EDIT BELOW THIS LINE * * */        (function() {            var dsq = document.createElement(&#39;script&#39;); dsq.type = &#39;text/javascript&#39;; dsq.async = true;            dsq.src = &#39;//&#39; + disqus_shortname + &#39;.disqus.com/embed.js&#39;;            (document.getElementsByTagName(&#39;head&#39;)[0] || document.getElementsByTagName(&#39;body&#39;)[0]).appendChild(dsq);        })();    </script>    <noscript>Please enable JavaScript to view the <a href=\"http://disqus.com/?ref_noscript\">comments powered by Disqus.</a></noscript>    ', 1, 1135436313),
