ads:\r\n\r\n [<a href=\"[*presentation_slides*]\">Slides</a>] \r\n [<a href=\"[*presentation_audio*]\">Audio</a>] \r\n [<a href=\"[*presentation_video*]\">Video</a>] \r\n [<a href=\"[*presentation_link*]\">Conference Link</a>] \r\n                </div>\r\n         </div>\r\n   </div>\r\n<script type=\"text/javascript\">\r\n    var audioTag = document.createElement(&#39;audio&#39;);\r\n    if (!(!!(audioTag.canPlayType) && (\"no\" != audioTag.canPlayType(\"audio/mpeg\")) && (\"\" != audioTag.canPlayType(\"audio/mpeg\")))) {\r\n        AudioPlayer.embed(\"audioplayer\", {soundFile: \"[*presentation_audio*]\"});\r\n    }\r\n</script>', 1, 1128783107),
